import fs from "fs";
import through from "through2";
import objectAssign from "object-assign";
import autoprefixer from "autoprefixer";
import CleanCSS from "clean-css";
import cssfmt from "cssfmt";
import postcss from "postcss";
import reporter from "postcss-reporter";
import stylelint from "stylelint";
import StyleStats from "stylestats";
import defaultConfig from "./default-config";

let currentConfig;

/**
 * Operate on written data, then read the result of each file
 * @param {Object} config - Nozomi config object (see: ./default-config.js)
 * @returns {Stream} Transform stream
 */
export default function(config) {
  currentConfig = objectAssign(defaultConfig, config);

  if (currentConfig.env !== void 0) {
    currentConfig.environment = currentConfig.env;
  }

  return through.obj(transform);
}

/**
 * Operate on written data, then read the result of each file
 * @param {Buffer} file - chunk
 * @param {string} encode - file encode
 * @param {Function} callback - callback function
 * @private
 */
function transform(file, encode, callback) {
  if (file.isNull()) {
    callback(null, file);
    return;
  }

  if (file.isStream()) {
    callback(Error("Streaming not supported"));
    return;
  }

  const postcssPlugins = activatePostCSSPlugins(currentConfig);
  const processor = postcss(postcssPlugins)
    .process(file.contents.toString(), {
      map : file.sourceMap ? {annotation: false} : false,
      from: file.path,
      to  : file.path
    });

  processor
    .then((res) => {
      if (/product(?:ion)?/.test(currentConfig.environment)) {
        const cleanCSS = new CleanCSS();
        const minified = cleanCSS.minify(res.css);

        file.contents = new Buffer(minified.styles);
      }

      if (currentConfig.stats) {
        let stats = new StyleStats(file.path);

        stats.parse((err, result) => {
          console.log(JSON.stringify(result, null, 2));
        });
      }

      setImmediate(callback, null, file);
    })
    .catch((err) => {
      setImmediate(callback, err);
    });
}

/**
 * Activating PostCSS plugins
 * @param {Object} config - Nozomi config object (see: ./default-config.js)
 * @returns {Function[]} PostCSS plugins list
 * @private
 */
function activatePostCSSPlugins(config) {
  const plugins = [];

  if (config.lintRules && config.lintRules.rules) {
    plugins.push(stylelint(config.lintRules));
  }

  if (config.browsers && config.browsers !== "") {
    plugins.push(autoprefixer(config.browsers));
  }

  plugins.push(cssfmt);
  plugins.push(reporter);

  return plugins;
}

import objectAssign from "object-assign";
import defaultOptions from "./default-options";
import Config from "../config";
import Nozomi from "../index";

/**
 * Create a new instance of the core CLI engine
 * @param {CLIEngineOptions} options - Nozomi CLI options object (see: ./default-options.js)
 */
export default class CLIEngline {
  constructor(options) {
    this.currentOptions = objectAssign(defaultOptions, options);
    this.config = new Config(this.currentOptions.config);
  }

  /**
   * Executes the current configuration on an array of file and directory names
   * @param {String[]} patterns - An array of file and directory names
   * @returns {Stream} Readable/Writable stream
   */
  executeOnFiles(patterns) {
    const nozomi = new Nozomi();

    if (patterns.length === 1) {
      return nozomi.src(patterns[0])
        .pipe(nozomi.build(this.config))
        .pipe(nozomi.dest(process.cwd()));
    } else if (patterns.length <= 2) {
      return nozomi.src(patterns[0])
        .pipe(nozomi.build(this.config))
        .pipe(nozomi.dest(patterns[1]));
    } else {
      console.error("specify paths of too many");
      return;
    }
  }
}

import CLIEngine from "./cli-engine";
import defaultOptions from "./default-options";
import options from "./options";
import pkg from "../../package.json";

export default {
  /**
   * Executes the CLI based on an array of arguments that is passed
   * @param {String[]} args - The arguments to process
   * @returns {Number} The exit code for the operation
   */
  execute: (args) => {
    console.log(args);
    let currentOptions;
    let stream;

    try {
      currentOptions = options.parse(args);
    } catch (error) {
      console.error(error.message);
      return 1;
    }

    const files = currentOptions._;

    if (currentOptions.version) {
      console.log("v" + pkg.version);
    } else if (currentOptions.help) {
      console.log(options.generateHelp());
    } else {
      const engine = new CLIEngine(translateOptions(currentOptions));
      stream = engine.executeOnFiles(files);
    }

    return 0;
  }
}

/**
 * Translates the CLI options into the options expected by the CLIEngine
 * @param {Object} cliOptions - The CLI options object (optionator format)
 * @returns {Object} The options object for the CLIEngine
 * @private
 */
function translateOptions(cliOptions) {
  return {
    config: cliOptions.config || defaultOptions.config,
    output: cliOptions.output || defaultOptions.output,
    watch : cliOptions.watch || defaultOptions.watch
  };
}

