import fs from "fs";
import vfs from "vinyl-fs";
import build from "./build";

export default class Nozomi {
  /**
   * vinyl-fs.src() wrapper
   * @param {Globs} globs - takes a glob string or an array of glob strings
   * @returns {Stream} Readable stream
   */
  src(globs) {
    return vfs.src(globs);
  }

  /**
   * Operate on written data, then read the result of each file
   * @param {Object} config - Nozomi config object (see: ./default-config.js)
   * @returns {Stream} Transform stream
   */
  build(config) {
    return build(config);
  }

  /**
   * vinyl-fs.dest() wrapper
   * @param {String} folder - takes a folder path
   * @returns {Stream} Readable/Writable stream
   */
  dest(folder) {
    return vfs.dest(folder);
  }
}

"use strict";

import path from "path";
import assert from "power-assert";
import File from "vinyl";

import build from "../src/build";

/** @test {Nozomi#build} */
describe("Nozomi#build", () => {
  const testCSSFile = new File({
    path: "test.css",
    contents: new Buffer("div {\n\tdisplay: flex;\n}")
  });

  it("work properly", (done) => {
    const stream = build();

    stream.on("data", (file) => {
      assert.strictEqual(/-/.test(file.contents.toString()), true);
      assert.strictEqual(file.relative, "test.css");
    });

    stream.on("end", done);

    stream.write(testCSSFile);

    stream.end();
  });
});

"use strict";

import fs from "fs";
import del from "del";
import assert from "power-assert";
import CLIEngine from "../src/cli/cli-engine";

/** @test {CLIEngine} */
describe("CLIEngine", () => {
  const outputFile = `${process.cwd()}/main.css`;

  afterEach((done) => {
    del(outputFile).then((paths) => {
      console.log("Deleted files/folders:\n", paths.join("\n"));
      done();
    }).catch((err) => {
      console.error(err);
      done();
    });
  });

  it("CSS file exists in process.cwd()", (done) => {
    const engine = new CLIEngine();
    engine.executeOnFiles([`${process.cwd()}/test/cases/main.css`]);

    setTimeout(() => {
      assert.doesNotThrow(() => fs.accessSync(outputFile, fs.R_OK), Error);
      done();
    }, 70);
  });
});

"use strict";

import assert from "power-assert";
import cli from "../src/cli/cli";

/** @test {cli} */
describe("cli", () => {
  /** @test {cli.execute} */
  describe("options", () => {
    it("throw error of run `nozomi` (empty)", () => {
      assert.strictEqual(cli.execute(), 1);
    });

    it("throw error of run `nozomi -z` (invalid option)", () => {
      assert.strictEqual(cli.execute("-z"), 1);
    });

    it("no error of run `nozomi -v`", () => {
      assert.strictEqual(cli.execute("-v"), 0);
    });

    it("no error of run `nozomi -h`", () => {
      assert.strictEqual(cli.execute("-h"), 0);
    });
  });
});
